CREATE TABLE IF NOT EXISTS fichas (
    `id` INT,
    `assunto` VARCHAR(82) CHARACTER SET utf8,
    `periodico` VARCHAR(36) CHARACTER SET utf8,
    `data_edicao` VARCHAR(9) CHARACTER SET utf8,
    `duracao_edicao` INT,
    `pagina` VARCHAR(17) CHARACTER SET utf8,
    `resumo` VARCHAR(450) CHARACTER SET utf8,
    `comentarios` VARCHAR(490) CHARACTER SET utf8,
    `edicao` VARCHAR(243) CHARACTER SET utf8
);
